
public class AssignmentOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c,d;
		a = 10;
		b=20;
		c=6;
		d=4;
		
		a +=20;
		b-=10;
		c*=5;
		d/=10;
		
		System.out.println("a= " +a);
		System.out.println("b= " +b);
		System.out.println("c= " +c);
		System.out.println("d= " +d);


	}

}
