package LoopProgram;

public class ForLoopTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		int sum=0;
		for(i=1;i<=50;i=i+2)
		{
			//System.out.println(i);
			sum=sum+i;
		}
		System.out.println("sum of 1 to 50 even natural number = " +sum);

	}

}
